/*Table structure for table `mer_order_detail` */

DROP TABLE IF EXISTS `mer_order_detail`;

CREATE TABLE `mer_order_detail` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '主键id',
  `mer_order_no` varchar(64) DEFAULT NULL COMMENT '商户订单号',
  `goods_id` varchar(64) DEFAULT NULL COMMENT '商品编号',
  `goods_desc` varchar(256) DEFAULT NULL COMMENT '商品描述',
  `goods_price` decimal(16,2) DEFAULT '0.00' COMMENT '商品单价',
  `goods_unit` varchar(64) DEFAULT NULL COMMENT '商品单位',
  `goods_amt` decimal(16,2) DEFAULT '0.00' COMMENT '商品金额',
  `version` int(11) DEFAULT '0' COMMENT '版本号',
  `create_time` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `modi_time` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '修改时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='商户订单详情表';

/*Data for the table `mer_order_detail` */

/*Table structure for table `mer_refund_order` */

DROP TABLE IF EXISTS `mer_refund_order`;

CREATE TABLE `mer_refund_order` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '主键id',
  `refund_no` varchar(64) DEFAULT NULL COMMENT '商户退款订单号',
  `refund_time` datetime DEFAULT NULL COMMENT '退款日期',
  `pay_no` varchar(64) DEFAULT NULL COMMENT '付款唯一号(商户订单号+商户支付流水号联合查询出来)',
  `trade_no` varchar(64) DEFAULT NULL COMMENT '交易号',
  `mer_no` varchar(64) DEFAULT NULL COMMENT '商户号',
  `mer_name` varchar(64) DEFAULT NULL COMMENT '商户名称',
  `mer_order_no` varchar(64) DEFAULT NULL COMMENT '商户订单号',
  `mer_pay_seq` varchar(64) DEFAULT NULL COMMENT '商户支付流水号',
  `user_no` varchar(64) DEFAULT NULL COMMENT '用户编号',
  `refund_way` varchar(32) DEFAULT NULL COMMENT '退款方式',
  `refund_status` int(11) DEFAULT '0' COMMENT '退款状态 0:退款中 1:退款成功 2:退款失败',
  `refund_amt` decimal(16,2) DEFAULT NULL COMMENT '退款金额',
  `refund_desc` varchar(128) DEFAULT NULL COMMENT '退款原因',
  `create_time` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `modi_time` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '修改时间',
  PRIMARY KEY (`id`),
  UNIQUE KEY `uniq_index` (`refund_no`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='退款收单表';

/*Data for the table `mer_refund_order` */

/*Table structure for table `pay_dict` */

DROP TABLE IF EXISTS `pay_dict`;

CREATE TABLE `pay_dict` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '主键id',
  `dict_code` varchar(32) DEFAULT NULL COMMENT '字典代码',
  `dict_value` varchar(64) DEFAULT NULL COMMENT '字典值',
  `dict_name` varchar(64) DEFAULT NULL COMMENT '字典名称',
  `dict_desc` varchar(128) DEFAULT NULL COMMENT '字典描述',
