DROP TABLE IF EXISTS `pay_refund_detail`;

CREATE TABLE `pay_refund_detail` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '主键id',
  `refund_seq_no` varchar(64) DEFAULT NULL COMMENT '退款流水号',
  `trade_no` varchar(64) DEFAULT NULL COMMENT '交易号',
  `pay_seq_no` varchar(64) DEFAULT NULL COMMENT '付款流水号',
  `user_no` varchar(64) DEFAULT NULL COMMENT '用户编号(系统内部使用)',
  `refund_way` varchar(16) DEFAULT NULL COMMENT '退款方式',
  `refund_amt` decimal(16,2) DEFAULT '0.00' COMMENT '退款金额',
  `refund_status` int(11) DEFAULT '0' COMMENT '退款状态  0:退款中 1:退款成功 2:退款失败',
  `refund_desc` varchar(128) DEFAULT NULL COMMENT '退款原因',
  `start_time` datetime DEFAULT NULL COMMENT '退款开始时间',
  `end_time` datetime DEFAULT NULL COMMENT '退款结束时间',
  `once_str` varchar(128) DEFAULT NULL COMMENT '随机号',
  `forward_url` varchar(128) DEFAULT NULL COMMENT '跳转地址',
  `payler_acct` varchar(64) DEFAULT NULL COMMENT '付款人账号',
  `seller_acct` varchar(64) DEFAULT NULL COMMENT '收款人账号',
  `refund_currency` varchar(16) DEFAULT NULL COMMENT '退款币种',
  `refund_success_time` datetime DEFAULT NULL COMMENT '退款成功时间',
  `buy_refund_amt` decimal(16,2) DEFAULT NULL COMMENT '买家实际退款金额',
  `third_refund_discount_amt` decimal(16,2) DEFAULT NULL COMMENT '第三方优惠退款金额',
  `mer_refund_discount_amt` decimal(16,2) DEFAULT NULL COMMENT '商户优惠退款金额',
  `resp_refund_no` varchar(64) DEFAULT NULL COMMENT '第三方退款单号（通知回调）',
  `mer_no` varchar(64) DEFAULT NULL COMMENT '商户号',
  `mer_name` varchar(64) DEFAULT NULL COMMENT '商户名称',
  `channel_code` varchar(64) DEFAULT NULL COMMENT '渠道编号与付款方式对应',
  `channel_name` varchar(64) DEFAULT NULL COMMENT '渠道名称 类似微信、支付宝',
  `channel_partener_no` varchar(64) DEFAULT NULL COMMENT '渠道合作号',
  `channel_partener_name` varchar(64) DEFAULT NULL COMMENT '渠道合作名称',
  `version` int(11) DEFAULT '0' COMMENT '版本号',
  `create_time` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `modi_time` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '修改时间',
  PRIMARY KEY (`id`),
  UNIQUE KEY `uniq_refund_no` (`refund_seq_no`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='退款明细表';

