/*Table structure for table `pay_trade_total` */

DROP TABLE IF EXISTS `pay_trade_total`;

CREATE TABLE `pay_trade_total` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '主键id',
  `trade_no` varchar(64) DEFAULT NULL COMMENT '交易号',
  `trade_time` datetime DEFAULT NULL COMMENT '交易日期',
  `mer_no` varchar(64) DEFAULT NULL COMMENT '商户号',
  `mer_name` varchar(64) DEFAULT NULL COMMENT '商户名称',
  `user_no` varchar(64) DEFAULT NULL COMMENT '用户编号',
  `trade_type` int(11) DEFAULT '0' COMMENT '交易类型 0:付款 1:退款',
  `trade_amt` decimal(16,2) DEFAULT NULL COMMENT '交易金额',
  `act_trade_amt` decimal(16,2) DEFAULT '0.00' COMMENT '实际交易金额',
  `trade_status` varchar(16) DEFAULT NULL COMMENT '交易状态 ',
  `create_time` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `modi_time` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '修改时间',
  PRIMARY KEY (`id`),
  UNIQUE KEY `uniq_trade_no` (`trade_no`)
) ENGINE=InnoDB AUTO_INCREMENT=78 DEFAULT CHARSET=utf8 COMMENT='交易汇总表';

