/*Table structure for table `product_channel_route` */

DROP TABLE IF EXISTS `product_channel_route`;

CREATE TABLE `product_channel_route` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '主键id',
  `mer_no` varchar(64) DEFAULT NULL COMMENT '商户编号',
  `product_no` varchar(32) DEFAULT NULL COMMENT '产品编码',
